// autenticação
import { useState, createContext, useEffect } from 'react';
import { auth, db } from '../services/firebaseConnection';
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
} from '@firebase/auth';
import { doc, getDoc, setDoc } from '@firebase/firestore';

import { useNavigate } from 'react-router';
import { toast } from 'react-toastify';

export const AuthContext = createContext({});
// O contexto foi projetado para compartilhar dados que podem ser considerados "globais"
function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loadingAuth, setLoadingAuth] = useState(false);
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate();

  useEffect(() => {
    async function loadUser() {
      //verifica se tem usuario e pega seus dados e joga dentro de setUser
      const storageUser = localStorage.getItem('ticketsPRO');

      if (storageUser) {
        setUser(JSON.parse(storageUser));
        setLoading(false);
      }
      setLoading(false);
    }

    loadUser();
  }, []);

  async function signIn(email, password) {
    setLoadingAuth(true);

    await signInWithEmailAndPassword(auth, email, password)
      .then(async (value) => {
        let uid = value.user.uid;

        const docRef = doc(db, 'users', uid);
        const docSnap = await getDoc(docRef);

        let data = {
          uid: uid,
          nome: docSnap.data().nome,
          email: value.user.email,
          avatarUrl: docSnap.data().avatarUrl,
        };

        setUser(data);
        storageUser(data);
        setLoadingAuth(false);
        toast.success('Bem-vindo(a)');
        navigate('/dashboard');
      })
      .catch((error) => {
        console.log(error);

        setLoadingAuth(false);
        toast.error('Ops, algo deu errado!');
      });
  }
  // cadastrar um novo user
  async function signUp(email, password, name) {
    setLoadingAuth(true);

    await createUserWithEmailAndPassword(auth, email, password)
      .then(async (value) => {
        let uid = value.user.uid;

        await setDoc(doc(db, 'users', uid), {
          nome: name,
          avatarUrl: null,
        }) // criando uma coleção de users e dentro criando doc de usuario
          .then(() => {
            let data = {
              uid: uid,
              nome: name,
              email: value.user.email,
              avatarUrl: null,
            };
            setUser(data);
            storageUser(data);
            setLoadingAuth(false);
            toast.success('Seja bem-vindo ao sistema!');
            navigate('/dashboard');
          });
      })
      .catch((error) => {
        console.log(error);
        setLoadingAuth(false);
      });
  }
  function storageUser(data) {
    localStorage.setItem('ticketsPRO', JSON.stringify(data));
  }

  return (
    <AuthContext.Provider
      value={{
        signed: !!user,
        user,
        signIn,
        signUp,
        loadingAuth,
        loading,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}
export default AuthProvider;
