// autenticação
import { useState, createContext, useEffect } from 'react';

export const AuthContext = createContext({});
function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  return (
    <AuthContext.Provider
      value={{
        signed: !!user,
        user,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}
export default AuthProvider;
